Setting up the Data

library(tidyverse)


attendance &lt;- readr::read_csv('https://raw.githubusercontent.com/rfordatascience/tidytuesday/main/data/2020/2020-02-04/attendance.csv')
games &lt;- readr::read_csv('https://raw.githubusercontent.com/rfordatascience/tidytuesday/main/data/2020/2020-02-04/games.csv')


# Data Prep
games &lt;- games |&gt;
  mutate(week = as.numeric(week))

nfl_data &lt;- games |&gt;
  left_join(attendance, by = c("home_team_city" = "team",
                               "year" = "year",
                               "week" = "week")) |&gt;
  filter(year == 2019) |&gt;
  select(week, home_team, day, date, time, weekly_attendance) |&gt;
  mutate(
    day = factor(day, levels = c("Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun")),
    date = mdy(paste0(date, ", ", 2019)),
    hour = hour(time),
    time_slot = case_when(
      hour &lt;= 13.5 ~ "Midday",
      hour &lt;= 17 ~ "Afternoon",
      hour &gt;= 19 ~ "Primetime",
      TRUE ~ NA_character_
    )
  ) |&gt;
  filter(!is.na(time_slot)) |&gt;
  mutate(time_slot = factor(time_slot, 
                            levels = c("Midday", "Afternoon", "Primetime")))
